import React from "react";
import { useLocation } from "react-router-dom";
import Cake from "../assets/birthdaycake.png";
import CalendarDaysIcon from "@heroicons/react/outline";

const Event = (props) => {
  const data = props.location.state;
  console.log(data);
  return (
    <div className="w-full bg-white">
      <div className="flex flex-col justify-center items-center">
        <img
          src={Cake}
          className="my-4 w-1/4 h-1/4 object-cover rounded-xl"
          alt="/"
        />
      </div>{" "}
      <div className="flex flex-col items-center">
        <p className="uppercase text-3xl tracking-widest text-[#240D57] mr-64">
          {data[0].eventName}
        </p>
        <p className="text-sm tracking-widest text-slate-600 mr-52">
          Hosted By: {data[0].hostName}
        </p>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          stroke-width="1.5"
          stroke="currentColor"
          class="w-6 h-6"
        >
          <path
            stroke-linecap="round"
            stroke-linejoin="round"
            d="M6.75 3v2.25M17.25 3v2.25M3 18.75V7.5a2.25 2.25 0 012.25-2.25h13.5A2.25 2.25 0 0121 7.5v11.25m-18 0A2.25 2.25 0 005.25 21h13.5A2.25 2.25 0 0021 18.75m-18 0v-7.5A2.25 2.25 0 015.25 9h13.5A2.25 2.25 0 0121 11.25v7.5m-9-6h.008v.008H12v-.008zM12 15h.008v.008H12V15zm0 2.25h.008v.008H12v-.008zM9.75 15h.008v.008H9.75V15zm0 2.25h.008v.008H9.75v-.008zM7.5 15h.008v.008H7.5V15zm0 2.25h.008v.008H7.5v-.008zm6.75-4.5h.008v.008h-.008v-.008zm0 2.25h.008v.008h-.008V15zm0 2.25h.008v.008h-.008v-.008zm2.25-4.5h.008v.008H16.5v-.008zm0 2.25h.008v.008H16.5V15z"
          />
        </svg>

        <p className="text-xl tracking-widest text-[#517be5]">
          {data[0].eventDescription}
        </p>
        <span>
          {data[0].startDate} to {data[0].endDate}
        </span>
      </div>
    </div>
  );
};

export default Event;
